function addition(...nombres) {
let somme = 0;
/*nombres.forEach(function (elem){
    somme += elem
}); */

nombres.forEach(element => somme += element)
return somme;
}

// exporter un fonction


function multiplication(...nombres) {
    let resultat = 1;
    nombres.forEach(elem => resultat *= elem);
    return resultat;
};

// Fonction principale d'un module
module.exports = function (operation, ...nombres){
   switch(operation){
   case 'addition':
       return addition(...nombres);
   break
   case 'multiplication':
        return multiplication(...nombres);
   break;
   default:
      return "Op√©ration non-connue";
   }

};

module.exports.addition = addition;
module.exports.multiplication = multiplication;
